import React, { Component } from 'react';
import $ from 'jquery'
import MovieRow from './MovieRow'

class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.performSearch();
    this.viewMovie = this.viewMovie.bind(this);
  }

  viewMovie(e) {
    const url = "http://www.themoviedb.org/movie/" + this.props.movie.id
    window.location.href = url
  }

  performSearch() {
    console.log("perform intial search");
    const urlString = "https://api.themoviedb.org/3/movie/top_rated?api_key=ddb907d69f1671fabdfd75c40b664a37&language=en-US&page=5"
    $.ajax({
      url: urlString,
      success: (searchResults) => {
        console.log("successfully fetched");
        //console.log(searchResults);
        const results = searchResults.results;
        var movieRows = [];
        results.forEach((movie) => {
          console.log(movie.title);
          const movieRow = <MovieRow movie = {movie}/>
          movieRows.push(movieRow);
        })
        this.setState({rows: movieRows})
      },
      error: (xhr, status, err) => {
        console.error("failed on fetch")
      }
    })

  }
  render() {
    const {movie} = this.props;
    return (

      <table key={this.props.movie.id}>
      <tbody>
        <tr>
          <td>
            <img alt="poster" width="120" src={'https://image.tmdb.org/t/p/w185'+this.props.movie.poster_path}/>
          </td>
          <td>
            <h3>{this.props.movie.title}</h3>
            <p>{this.props.movie.overview}</p>
          <input type="button" className="btn btn-primary" onClick={this.viewMovie} value="View"/>
          </td>
        </tr>
      </tbody>
    </table>
    );
  }


}

export default HomePage
